// ==UserScript==
// @name           TwitterAgain
// @description    Twitter favicon Again
// @version        0.20240517.0
// @namespace      https://github.com/s-kono/UserScript
// @author         github.com/s-kono
// @match          https://twitter.com/*
// @match          https://x.com/*
// @grant          none
// @run-at         document-idle
// @icon           data:image/x-icon;base64,
// @updateURL      https://github.com/s-kono/UserScript/raw/main/TwitterAgain.user.js
// @downloadURL    https://github.com/s-kono/UserScript/raw/main/TwitterAgain.user.js
// ==/UserScript==

(function() {
    'use strict';
    const us_name = 'TwitterAgain'

    function formatDate(date) {
        const yyyy = date.getFullYear();
        const mm = ('0' + (date.getMonth()+1)).slice(-2);
        const dd = ('0' + date.getDate()).slice(-2);
        const hhmmss = ('0' + date.getHours()).slice(-2) + ':' + ('0' + date.getMinutes()).slice(-2) + ':' + ('0' + date.getSeconds()).slice(-2);
        return (yyyy + '-' + mm + '-' + dd + ' ' + hhmmss);
    }

    const css = `
div[data-testid="tweetText"] {
    -webkit-line-clamp: initial !important;
}
span {
    font-family: 'BIZ UDPGothic', 'Meiryo UI', sans-serif !important;
}
div[data-testid="card.wrapper"] div:has(> div > span) {
    border: 1px solid white;
}
div[data-testid="cellInnerDiv"]:has(svg[data-testid="icon-verified"]) {
    background-color: #023;
}
div[aria-label="タイムライン: 会話"] div[data-testid="cellInnerDiv"]:has(h2) {
    border: 3px dotted red;
    z-index: 999;
    margin: -3px;
}
    `;
    const style = document.createElement('style');
    style.type = 'text/css';
    style.id = us_name;
    style.appendChild(document.createTextNode(css));

    //https://about.twitter.com/etc/designs/about2-twitter/public/img/favicon.ico
    //const favicon = 'data:image/x-icon;base64,';
    //
    const favicon = 'data:image/x-icon;base64,';

    let feed_observer;
    function mod() {
        console.log('[' + us_name + '] <' + formatDate(new Date()) + '> mod()');

        for(const div of document.querySelectorAll('div[data-testid="cellInnerDiv"] div[role="button"]')) {
            if(div.innerText.match(/返信を表示|返信をさらに表示/)){
                div.click();
            }
        }

        try {
            feed_observer.disconnect();
        } catch(e) {
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> mod():feed_observer.disconnect().error: ' + String(e.message));
        }
        feed_observer = new MutationObserver(function(mutations) {
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> detect:feed_update');
            mod();
        });
        try {
            feed_observer.observe(document.querySelector('div[data-testid="primaryColumn"] section'), { childList: true, subtree: true, characterData: false, attributes: false });
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> mod(): set feed_observer');
        } catch(e) {
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> mod():feed_observer.observe().error!!!!!!!!!: ' + String(e.message));
            setTimeout(() => {
                mod();
            }, 1000);
            return;
        }
    }

    let title_observer;
    function init() {
        console.log('[' + us_name + '] init() [' + formatDate(new Date()) + ']');

        if(document.head.querySelector('#' + us_name) === null) {
            document.head.appendChild(style);
        }
        //document.querySelector("head > link[rel='shortcut icon']").href = favicon;

        try {
            title_observer.disconnect();
        } catch(e) {
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> init():title_observer.disconnect().error: ' + String(e.message));
        }
        try {
            title_observer = new MutationObserver(function(mutations) {
                console.log('[' + us_name + '] title_observer [' + formatDate(new Date()) + ']');
                init();
            });
            title_observer.observe(document.querySelector('title'), { childList: true, subtree: false, characterData: true, attributes: false });
            if(document.querySelector("title").textContent.match(/^Xユーザーの/)) {
                document.querySelector("title").textContent = document.querySelector("title").textContent.replace(/^Xユーザーの(.+)さん(: )/, '$1$2');
            }
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> init(): set title_observer');
        } catch(e) {
            console.log('[' + us_name + '] <' + formatDate(new Date()) + '> init().error: ' + String(e.message));
            setTimeout(() => {
                init();
            }, 500);
            return;
        }

        setTimeout(() => {
            mod();
        }, 500);
    }

    init();
})();
