// ==UserScript==
// @name           font
// @description    mod font CSS
// @version        0.20220904.0
// @namespace      https://github.com/s-kono/UserScript
// @author         github.com/s-kono
// @match          <all_urls>
// @match          *://*/*
// @grant          none
// @run-at         document-idle
// @icon           data:image/x-icon;base64,
// @downloadURL    https://github.com/s-kono/UserScript/raw/main/font.user.js
// @updateURL      https://github.com/s-kono/UserScript/raw/main/font.user.js
// ==/UserScript==

(function() {
    'use strict';
    const css = `
body {
    font-family: "FirgeNerd", "PlemolJP", "Terminus-ja", "MJ-UD新ゴ Pr6N", "メイリオ", sans-serif !important;
}
pre, code {
    font-family: "FirgeNerd", "PlemolJP", "Terminus-ja", "BIZ UDゴシック", "UD デジタル 教科書体 N-R", "MS ゴシック", sans-serif !important;
}
    `;
    const style = document.createElement('style');
    style.type = 'text/css';
    style.appendChild(document.createTextNode(css));
    document.head.appendChild(style);
})();
