// ==UserScript==
// @name           font
// @description    mod font
// @version        0.20221226.0
// @namespace      https://github.com/s-kono/UserScript
// @author         github.com/s-kono
// @match          <all_urls>
// @match          *://*/*
// @grant          none
// @run-at         document-idle
// @icon           data:image/x-icon;base64,
// @downloadURL    https://github.com/s-kono/UserScript/raw/main/font.user.js
// @updateURL      https://github.com/s-kono/UserScript/raw/main/font.user.js
// ==/UserScript==

(function() {
    'use strict';
    const css = `
body, h1, h2, h3, h4, div, p {
    font-family: 'MJ-UD新ゴ Pr6N', 'BIZ UDPGothic', 'メイリオ', sans-serif !important;
}
pre, code, textarea, time, a {
    font-family: 'FirgeNerd', 'PlemolJP', 'Terminus-ja', 'BIZ UDGothic', 'UD デジタル 教科書体 N-R', 'MS ゴシック', sans-serif !important;
}
    `;
    const style = document.createElement('style');
    style.type = 'text/css';
    style.appendChild(document.createTextNode(css));
    document.head.appendChild(style);
    let link;
    link = document.createElement('link');
    link.rel = 'preconnect';
    link.href = 'https://fonts.googleapis.com';
    document.head.appendChild(link);
    link = document.createElement('link');
    link.setAttribute('crossorigin', '');
    link.rel = 'preconnect';
    link.href = 'https://fonts.gstatic.com';
    document.head.appendChild(link);
    link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://fonts.googleapis.com/css2?family=BIZ+UDGothic:wght@400;700&family=BIZ+UDPGothic:wght@400;700&display=swap';
    document.head.appendChild(link);
})();
